import java.util.HashMap;
import java.util.Map;

public class prblm657 {
    public static void main(String[] args) {
        String moves = "UD";
        System.out.println(new prblm657().judgeCircle(moves));
        
        String moves2 = "LL";
        System.out.println(new prblm657().judgeCircle(moves2));
        
        String moves3 = "";
        System.out.println(new prblm657().judgeCircle(moves3));
    }
    
    public boolean judgeCircle(String moves) {
        Map<Character, Integer> map = new HashMap<>();
        for(char ch : moves.toCharArray()){
            map.put(ch, map.getOrDefault(ch, 0) + 1);
        }
        return (map.getOrDefault('L', 0).equals(map.getOrDefault('R', 0))) && (map.getOrDefault('U', 0).equals(map.getOrDefault('D', 0)));
    }

    public boolean judgeCircle2(String moves) {
        int x = 0, y = 0;
        for (char ch : moves.toCharArray()) {
            if (ch == 'U') y++;
            else if(ch == 'D') y--;
            else if(ch == 'L') x++;
            else if(ch == 'R') x--;
        }
        return x == 0 && y == 0;
    }
}
